import React, { useState, useEffect } from 'react';
import { View, StyleSheet } from 'react-native';
import { GiftedChat } from 'react-native-gifted-chat';

const ChatScreen = () => {
  const [messages, setMessages] = useState([]);

  useEffect(() => {
    // Cargar mensajes iniciales (puedes obtenerlos de tu propio servidor o fuente)
    loadInitialMessages();
  }, []);

  // Función para cargar mensajes iniciales (simulación)
  const loadInitialMessages = () => {
    // Simula la carga de mensajes iniciales
    const initialMessages = [
      {
        _id: 1,
        text: '¡Bienvenido al chat público!',
        createdAt: new Date(),
        user: {
          _id: 2,
          name: 'Chat Bot',
        },
      },
    ];
    setMessages(initialMessages);
  };

  // Función para enviar un nuevo mensaje
  const onSend = (newMessage) => {
    setMessages((previousMessages) => GiftedChat.append(previousMessages, newMessage));
  };

  return (
    <View style={styles.container}>
      <GiftedChat
        messages={messages}
        onSend={(newMessage) => onSend(newMessage)}
        user={{ _id: 1, name: 'Tu Nombre' }}
      />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
});

export default ChatScreen;
