import React, { useState } from 'react';
import { View, Text, StyleSheet, TextInput, TouchableOpacity } from 'react-native';
import Mailer from 'react-native-mail';

const Horarios = () => {
  const [subject, setSubject] = useState('Solicitud de Reunión');
  const [recipient, setRecipient] = useState('correo@destinatario.com'); // Correo del destinatario
  const [body, setBody] = useState('');

  const sendMeetingRequest = () => {
    Mailer.mail(
      {
        subject,
        recipients: [recipient],
        body,
      },
      (error, event) => {
        if (error) {
          console.error('Error al enviar el correo electrónico:', error);
        }
      }
    );
  };

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Solicitud de Reunión</Text>
      <TextInput
        style={styles.input}
        placeholder="Asunto"
        value={subject}
        onChangeText={(text) => setSubject(text)}
      />
      <TextInput
        style={styles.input}
        placeholder="Destinatario"
        value={recipient}
        onChangeText={(text) => setRecipient(text)}
      />
      <TextInput
        style={styles.bodyInput}
        placeholder="Cuerpo del Correo Electrónico"
        value={body}
        multiline
        onChangeText={(text) => setBody(text)}
      />
      <TouchableOpacity
        style={styles.button}
        onPress={sendMeetingRequest}
      >
        <Text style={styles.buttonText}>Enviar Solicitud</Text>
      </TouchableOpacity>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    padding: 20,
    backgroundColor: '#FFFDED',
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 16,
  },
  input: {
    width: '100%',
    borderWidth: 1,
    borderColor: '#ccc',
    borderRadius: 5,
    padding: 8,
    marginBottom: 16,
  },
  bodyInput: {
    width: '100%',
    borderWidth: 1,
    borderColor: '#ccc',
    borderRadius: 5,
    padding: 8,
    marginBottom: 16,
    height: 150, // Altura suficiente para un cuadro de texto grande
  },
  button: {
    backgroundColor: '#00BFFF',
    borderRadius: 10,
    padding: 10,
  },
  buttonText: {
    color: 'white',
    fontSize: 18,
    textAlign: 'center',
  },
});

export default Horarios;
