import React, { useState, useEffect } from 'react';
import { View, Text, TextInput, Button, FlatList } from 'react-native';
import AsyncStorage from '@react-native-async-storage/async-storage';

const ChatScreen = () => {
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [username, setUsername] = useState('Usuario An贸nimo');

  useEffect(() => {
    // Carga los mensajes almacenados en AsyncStorage cuando el componente se monta
    loadMessages();
  }, []);

  // Funci贸n para cargar mensajes almacenados en AsyncStorage
  const loadMessages = async () => {
    try {
      const storedMessages = await AsyncStorage.getItem('chatMessages');
      if (storedMessages) {
        setMessages(JSON.parse(storedMessages));
      }
    } catch (error) {
      console.error('Error loading messages:', error);
    }
  };

  // Funci贸n para guardar mensajes en AsyncStorage y actualizar el estado
  const saveMessages = async (messagesToSave) => {
    try {
      await AsyncStorage.setItem('chatMessages', JSON.stringify(messagesToSave));
      setMessages(messagesToSave);
    } catch (error) {
      console.error('Error saving messages:', error);
    }
  };

  const sendMessage = () => {
    if (newMessage.trim() !== '') {
      const updatedMessages = [...messages, { text: newMessage, username }];
      saveMessages(updatedMessages);
      setNewMessage('');
    }
  };

  return (
    <View>
      <Text>Chat Simple</Text>
      <FlatList
        data={messages}
        renderItem={({ item }) => (
          <Text>
            {item.username}: {item.text}
          </Text>
        )}
        keyExtractor={(item, index) => index.toString()}
      />
      <TextInput
        placeholder="Nombre de usuario"
        value={username}
        onChangeText={(text) => setUsername(text)}
      />
      <TextInput
        placeholder="Escribe un mensaje"
        value={newMessage}
        onChangeText={(text) => setNewMessage(text)}
      />
      <Button title="Enviar" onPress={sendMessage} />
    </View>
  );
};

export default ChatScreen;
