import React, { useState, useEffect } from 'react';
import { View, Text, StyleSheet, TextInput, Button, FlatList, ScrollView } from 'react-native';
import AsyncStorage from '@react-native-async-storage/async-storage';

const Tutores = () => {
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');

  // Cargar mensajes almacenados en AsyncStorage cuando el componente se monta
  useEffect(() => {
    loadMessages();
  }, []);

  // Función para cargar mensajes almacenados en AsyncStorage
  const loadMessages = async () => {
    try {
      const storedMessages = await AsyncStorage.getItem('chatMessages');
      if (storedMessages) {
        setMessages(JSON.parse(storedMessages));
      }
    } catch (error) {
      console.error('Error loading messages:', error);
    }
  };

  // Función para guardar mensajes en AsyncStorage y actualizar el estado
  const saveMessages = async (messagesToSave) => {
    try {
      await AsyncStorage.setItem('chatMessages', JSON.stringify(messagesToSave));
      setMessages(messagesToSave);
    } catch (error) {
      console.error('Error saving messages:', error);
    }
  };

  const handleSendMessage = () => {
    if (newMessage.trim() !== '') {
      const updatedMessages = [...messages, { text: newMessage }];
      saveMessages(updatedMessages);
      setNewMessage('');
    }
  };

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Chat con Tutores</Text>
      <FlatList
        data={messages}
        renderItem={({ item }) => (
          <View style={styles.messageContainer}>
            <Text style={styles.messageText}>{item.text}</Text>
          </View>
        )}
        keyExtractor={(item, index) => index.toString()}
      />
      <TextInput
        style={styles.input}
        placeholder="Escribe un mensaje"
        value={newMessage}
        onChangeText={(text) => setNewMessage(text)}
      />
      <Button title="Enviar" onPress={handleSendMessage} />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 16,
    justifyContent: 'center',
    backgroundColor: '#F0F0F0',
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 16,
  },
  messageContainer: {
    backgroundColor: '#E3E3E3',
    borderRadius: 8,
    padding: 8,
    marginBottom: 8,
    maxWidth: '80%', // Limitar el ancho para que no sea demasiado amplio
    alignSelf: 'flex-start', // Alinear mensajes a la izquierda
  },
  messageText: {
    fontSize: 16,
  },
  input: {
    borderWidth: 1,
    borderColor: '#000',
    borderRadius: 8,
    padding: 8,
    marginBottom: 8,
  },
});

export default Tutores;
